def recommend_car(category, budget_input):
      #Filter cars by category
    if category not in car_data:
        return None
    
    # Process budget input (accepts both "15L" and "15-17L" formats)
    try:
        if '-' in budget_input:
            min_budget = float(budget_input.split('-')[0].replace('L', '')) * 100000
        else:
            min_budget = float(budget_input.replace('L', '')) * 100000
    except:
        return None
    
    # Filter by budget
    suitable_cars = []
    for car in car_data[category]:
        # Get car's min price (convert "â‚¹10-20L" to 1000000)
        car_min_price = float(car['price'].replace('â‚¹', '').replace('L', '').split('-')[0]) * 100000
        if car_min_price <= min_budget:
            suitable_cars.append(car)
    
    # If no cars in budget, return the cheapest in category
    if not suitable_cars and car_data[category]:
        suitable_cars = [car_data[category][0]]
    
    return suitable_cars[0] if suitable_cars else None

# Main app
def main():
    # Title
    st.markdown('<div class="title">NOJO Car Finder</div>', unsafe_allow_html=True)
    
    # Initialize session state
    if 'show_results' not in st.session_state:
        st.session_state.show_results = False
    if 'user_category' not in st.session_state:
        st.session_state.user_category = ""
    if 'user_budget' not in st.session_state:
        st.session_state.user_budget = ""

    if not st.session_state.show_results:
        # Input form
        with st.form("car_form"):
            st.session_state.user_category = st.selectbox(
                "Category", 
                ["SUV", "Sedan", "Hatchback", "Luxury", "XUV"], 
                key="category_select"
            )
            
            st.session_state.user_budget = st.text_input(
                "Budget (e.g., 15L or 15-17L)", 
                key="budget_input",
                placeholder="15L or 15-17L"
            )
            st.markdown('<div class="budget-hint">Enter your budget in lakhs (e.g., 15L for 15 lakhs or 15-17L for a range)</div>', unsafe_allow_html=True)
            
            submitted = st.form_submit_button("Find My Car")
            
            if submitted:
                if st.session_state.user_budget and st.session_state.user_budget[-1].upper() == 'L':
                    st.session_state.show_results = True
                    st.experimental_rerun()
                else:
                    st.error("Please enter budget in the correct format (e.g., 15L or 15-17L)")
    else:
        # Show recommendations
        recommended_car = recommend_car(
            st.session_state.user_category,
            st.session_state.user_budget
        )
        
        if recommended_car:
            st.markdown("## Here's Your Perfect Car Match!")
            
            with st.container():
                col1, col2 = st.columns([1, 2])
                
                with col1:
                    st.image(recommended_car["image"], caption=recommended_car["model"], use_container_width=True)
                
                with col2:
                    st.markdown(f"### {recommended_car['model']}")
                    st.markdown(f"**Price Range:** {recommended_car['price']}")
                    st.markdown(f"**Mileage:** {recommended_car['range']}")
        
        else:
            st.warning("Sorry, we couldn't find a car matching your criteria. Please try adjusting your budget or category.")
        
        if st.button("Start New Search"):
            st.session_state.show_results = False
            st.experimental_rerun()

if __name__ == "__main__":
    main()
